import sys
import Bio
from Bio import SeqIO
from Bio import AlignIO
from Bio import pairwise2
from Bio.pairwise2 import format_alignment
import csv

# system waits for user to import a file
# input_file = sys.stdin
# input_file = "spikeprot0129.fasta" # UNCOMMENT FOR OFFICIAL 
input_file = "hi.fasta"

# Define the reference sequence: Below is the actual COVID Spike Protein
# refseq_record = ''
# this is just a simple sequence for testing
refseq_record = 'ithinkpuppiesareverycoolHHHHHHHHHHHHHHHHHHHIIIIVWEOGBWNEO' # all of the sequences we want to compare to. 

# all of the sequences we want to compare to. 
seq_records = SeqIO.parse(input_file, 'fasta')

# read in each sequence from the fasta file
for seq_record in seq_records:
    # align the sequence using the dynamic programming global alignment approach. 
    #     points for a match = 2
    #     points for a mismatch = -1
    #     points opening a gap = -4
    #     points for extending a gap = -4
    alignments = pairwise2.align.globalms(refseq_record, seq_record.seq, 2, -1, -4, -4, one_alignment_only = True)
    # print(len(alignments)) # sanity check: only using one alignment 
    insertions = []
    deletions = []
    substitutions = []
    
    # for each refSeq:Seq alignment produced this way...
    for a in alignments:
        align = a[0:2]
        print(format_alignment(*a)) # Uncomment if you want to see the alignment
        for i in range(0, len(a[0])):
            nt1 = a[0].upper()
            nt1 = nt1[i]
            nt2 = a[1].upper()
            nt2 = nt2[i]
            if nt1 != nt2:
                if nt2 == "-":
                    deletion = "deletion," + str(nt1) + str(i+1) + str(nt2)
                    deletions.append(deletion)
                elif nt1 == "-":
                    insertion = "insertion,"+ str(nt2) + str(i+1) + str(nt2)
                    insertions.append(insertion)
                else:
                    substitution = "substitution," + str(nt1) + str(i+1) +  str(nt2)
                    substitutions.append(substitution)
        print("loading", seq_record.id)

mutations = insertions + substitutions + deletions

with open('GISAIDmutations.csv', 'w') as f: 
      
    # using csv.writer method from CSV package 
    write = csv.writer(f) 
    write.writerows(mutations) 
    
